#!/bin/bash\n\n# Function to generate a CSS variable name from a color value\ngenerate_var_name() {\n    local color=$1\n    local type=$2\n    echo "--color-$(echo $color | tr -d "#" | tr "[:upper:]" "[:lower:]")"\n}\n\n# Create a new CSS variables file if it doesnt exist\ntouch src/styles/colors.css\n\n# Process each found color and create variables\nprocess_colors() {\n    while IFS=: read -r file line match; do\n        if [[ $match =~ "#([0-9a-fA-F]{3}|[0-9a-fA-F]{6})" ]]; then\n            color="${BASH_REMATCH[0]}"\n            var_name=$(generate_var_name "$color" "hex")\n            echo "$var_name: $color;" >> src/styles/colors.css\n            sed -i "" "s/$color/var($var_name)/g" "$file"\n        fi\n    done\n}\n\n# Run the search and pipe to process_colors\n./search_colors.sh | process_colors\n\n# Add import statement to main CSS file\nif ! grep -q "@import.*colors.css" src/app/globals.css; then\n    sed -i "" "1i\\\\@import '../styles/colors.css';" src/app/globals.css\nfi
