#!/bin/bash\n\n# Check for remaining hard-coded colors\necho "Checking for remaining hard-coded colors..."\n\n# Count occurrences of different color formats\nhex_count=$(rg -c "(?<!-)#([0-9a-fA-F]{3}|[0-9a-fA-F]{6})(?![0-9a-fA-F])" --type css --type tsx --type jsx --type ts --type js | awk -F ":" "{sum += \$2} END {print sum}")\nrgb_count=$(rg -c "(?<!var\()rgb(a)?\([^)]+\)" --type css --type tsx --type jsx --type ts --type js | awk -F ":" "{sum += \$2} END {print sum}")\nnamed_count=$(rg -c "\b(red|blue|green|yellow|purple|orange|black|white|gray|grey)\b" --type css --type tsx --type jsx --type ts --type js | awk -F ":" "{sum += \$2} END {print sum}")\n\n# Check if colors.css exists and has content\nif [ -f "src/styles/colors.css" ]; then\n    var_count=$(grep -c "^--color-" src/styles/colors.css)\n    echo "Found $var_count color variables defined"\nelse\n    echo "Warning: colors.css not found!"\nfi\n\necho "Found:"\necho "- $hex_count hex colors"\necho "- $rgb_count rgb/rgba colors"\necho "- $named_count named colors"\n\n# Check if any files are missing the import\necho "\nChecking for files missing color variable imports..."\nfind . -type f -name "*.css" ! -name "colors.css" -exec grep -L "@import.*colors.css" {} \;
